import {create} from "zustand/react";
import {createJSONStorage, persist} from "zustand/middleware";

export const useAuthStore = create()(
  persist(
    (set) => ({
      isLoggedIn: false,
      roles: ["ANONYMOUS"],
      accessToken: null,
      login: (accessToken, roles) =>
        set({ isLoggedIn: true, roles: roles, accessToken: accessToken }),
      logout: () => {
        set({ isLoggedIn: false, roles: ["ANONYMOUS"], accessToken: null});
      },

    }),
    {
      name: 'auth-storage', // sessionStorage 내 key 이름
      storage: createJSONStorage(() => localStorage), // sessionStorage 사용
    },
  ),
);
