import axios from 'axios';
import { useAuthStore } from '../stores/authStore';


// withCredentials: true가 없으면:
//
//     브라우저는 http://localhost:8083으로 요청을 보낼 때, http://localhost:8083 도메인에 저장된 쿠키를 요청 헤더에 포함하지 않습니다.
//     http://localhost:8083에서 Set-Cookie 헤더를 포함한 응답이 오더라도, 브라우저는 해당 쿠키를 저장하지 않습니다.

export const client = axios.create({
  withCredentials: true, // `credentials`가 아니라 `withCredentials`입니다.
  baseURL: 'http://localhost:8083', // 필요하다면 여기에 백엔드 기본 URL을 설정할 수 있습니다.
});

//요청 인터셉터
client.interceptors.request.use((config) => {
  const token = useAuthStore.getState().accessToken;
  if (token) {
    config.headers['Authorization'] = `Bearer ${token}`;
  }
  return config;
});
